package com.ejie.aa79b.model;

import java.io.Serializable;
import java.util.Date;

import org.springframework.context.i18n.LocaleContextHolder;

import com.ejie.aa79b.utils.DateUtils;
import com.ejie.x38.serialization.JsonDateDeserializer;
import com.ejie.x38.serialization.JsonDateSerializer;
import com.fasterxml.jackson.databind.annotation.JsonDeserialize;
import com.fasterxml.jackson.databind.annotation.JsonSerialize;

/**
 * AsignadoProveedor generated by UDA, 17-nov-2017 13:04:12.
 * 
 * @author UDA
 */
public class FechaHoraIniFin implements Serializable {

    private static final long serialVersionUID = 1L;
    private Date fechaIni;
    private Date fechaFin;
    private String horaIni;
    private String horaFin;

    @JsonSerialize(using = JsonDateSerializer.class)
    public Date getFechaIni() {
        return fechaIni;
    }

    @JsonDeserialize(using = JsonDateDeserializer.class)
    public void setFechaIni(Date fechaIni) {
        this.fechaIni = fechaIni;
    }

    public String getHoraIni() {
        return this.horaIni;
    }

    public void setHoraIni(String horaIni) {
        this.horaIni = horaIni;
    }

    @JsonSerialize(using = JsonDateSerializer.class)
    public Date getFechaFin() {
        return fechaFin;
    }

    @JsonDeserialize(using = JsonDateDeserializer.class)
    public void setFechaFin(Date fechaFin) {
        this.fechaFin = fechaFin;
    }

    public String getHoraFin() {
        return this.horaFin;
    }

    public void setHoraFin(String horaFin) {
        this.horaFin = horaFin;
    }

    public String getFechaHoraIni() {
        return DateUtils.obtFechaHoraFormateada(this.fechaIni, this.horaIni, LocaleContextHolder.getLocale());
    }

    public String getFechaHoraFin() {
        return DateUtils.obtFechaHoraFormateada(this.fechaFin, this.horaFin, LocaleContextHolder.getLocale());
    }

    @Override
    public String toString() {
        return "FechaHoraIniFin [fechaIni=" + fechaIni + ", fechaFin=" + fechaFin + ", horaIni=" + horaIni
                + ", horaFin=" + horaFin + "]";
    }

}