@Service(value = "festivosCalendarioServicioService")
public class FestivosCalendarioServicioServiceImpl extends GenericoServiceImpl<FestivosCalendarioServicio>
        implements FestivosCalendarioServicioService {

    @Autowired
    private FestivosCalendarioServicioDao festivosCalendarioServicioDao;

    @Override
    protected GenericoDao<FestivosCalendarioServicio> getDao() {
        return this.festivosCalendarioServicioDao;
    }

    @Override()
    @Transactional(rollbackFor = Throwable.class)
    public FestivosCalendarioServicio add(FestivosCalendarioServicio festivosCalendarioServicio) {

        festivosCalendarioServicio.setIdDiaCalendario023(Integer.valueOf(DateFormaterFactory.getInstance()
                .getFechaCortaSinBarras().parse(festivosCalendarioServicio.getDiaCalendario023())));
        return this.festivosCalendarioServicioDao.add(festivosCalendarioServicio);
    }

    @Override
    public void removeTodosPorAnio(FestivosCalendarioServicio filterFestivosCalendarioServicio) {
        this.festivosCalendarioServicioDao.removeTodosPorAnio(filterFestivosCalendarioServicio);
    }

}
