package com.ejie.aa79b.service;

import java.math.BigDecimal;
import java.util.List;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import com.ejie.aa79b.dao.SubidaFicherosDao;
import com.ejie.aa79b.model.Fichero;
import com.ejie.aa79b.model.enums.TipoSubidaEnum;
import com.ejie.aa79b.model.webservices.Aa79bSalidaDatosDescargaDocumentos;

/**
 * SubidaFicherosServiceImpl generated by UDA, 06-feb-2018 10:13:42.
 * 
 * @author javarona
 */

@Service(value = "subidaFicherosService")
public class SubidaFicherosServiceImpl implements SubidaFicherosService {

	@Autowired()
	private SubidaFicherosDao subidaFicherosDao;

	@Override
	public Fichero find(int tipoDescarga, BigDecimal idFichero) {

		final TipoSubidaEnum tipoSubidaEnum = TipoSubidaEnum.getById(tipoDescarga);
		return this.subidaFicherosDao.find(tipoSubidaEnum, idFichero);
	}

	@Override
	public Aa79bSalidaDatosDescargaDocumentos findWS(int tipoDescarga, List<BigDecimal> listaIdsFicheros) {

		final TipoSubidaEnum tipoSubidaEnum = TipoSubidaEnum.getById(tipoDescarga);

		return this.subidaFicherosDao.findWS(tipoSubidaEnum, listaIdsFicheros);
	}

}
