package com.ejie.aa79b.service;

import java.util.List;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.i18n.LocaleContextHolder;
import org.springframework.stereotype.Service;

import com.ejie.aa79b.dao.GenericoDao;
import com.ejie.aa79b.dao.TiposDocumentoDao;
import com.ejie.aa79b.model.TiposDocumento;
import com.ejie.x38.dto.JQGridRequestDto;

/**
 * TiposDocumentoServiceImpl generated by UDA, 30-nov-2017 16:05:35.
 *
 * @author UDA
 */

@Service(value = "tiposDocumentoService")
public class TiposDocumentoServiceImpl extends GenericoServiceImpl<TiposDocumento> implements TiposDocumentoService {

	@Autowired
	private TiposDocumentoDao tiposDocumentoDao;

	@Override
	protected GenericoDao<TiposDocumento> getDao() {
		return this.tiposDocumentoDao;
	}

	@Override
	public List<TiposDocumento> findAll(TiposDocumento bean, JQGridRequestDto jqGridRequestDto) {
		if (jqGridRequestDto == null) {
			jqGridRequestDto = new JQGridRequestDto();
			String lang = LocaleContextHolder.getLocale().getLanguage().toUpperCase();
			jqGridRequestDto.setSidx("ORDEN_042 ASC, DESC_"+lang+"_042");
			jqGridRequestDto.setSord("ASC");
		}
		return this.getDao().findAll(bean, jqGridRequestDto);
	}
}
