@Service(value = "expedienteInterpretacionService")
public class ExpedienteInterpretacionServiceImpl extends GenericoServiceImpl<Expediente>
		implements ExpedienteInterpretacionService {

	@Autowired
	private ExpedienteInterpretacionDao expedienteInterpretacionDao;
	@Autowired
	private ExpedienteTradRevDao expedienteTradRevDao;

	@Override
	protected GenericoDao<Expediente> getDao() {
		return this.expedienteInterpretacionDao;
	}

	@Override
	public SubsanacionExpediente findDatosEstadoPresupuesto(ExpedienteTradRev bean) {
		return this.expedienteTradRevDao.findDatosEstadoPresupuesto(bean, TipoExpedienteEnum.INTERPRETACION.getValue());
	}

	@Override
	public ExpedienteTradRev updateDatosAceptacionPpto(ExpedienteTradRev bean, String idioma) {
		return this.expedienteTradRevDao.updateDatosAceptacionPpto(bean, idioma,
				TipoExpedienteEnum.INTERPRETACION.getValue());
	}
}
